import { style } from "typestyle";

const HeroSeccionStyle = style({
    position: "relative",
    display: 'flex',
    height: '120vh',
    $nest: {
        '& .hero_title__container': {
            opacity: 0,
            display: 'flex',
            flexDirection: "column",
            justifyContent: 'start',
            alignItems: 'start',
            width: '65%',
            paddingTop: '200px',
            paddingLeft: '25%',
            backgroundColor: '#00CFBE',
            $nest: {
                '& .hero_title': {
                    fontSize: '58px',
                    fontFamily: 'CocoGothLight',
                    lineHeight: '1',
                    color: '#202230',
                    marginBottom: '0px',
                },
                '& .hero_subtitle': {
                    color: 'white',
                    fontSize: '60px',
                    textTransform: 'uppercase',
                    fontFamily: 'CocoGothSemiBold',
                    lineHeight: '1',
                    margin: '30px 0',
                },
                '& .hero_suscribe__button': {
                    textAlign: "center",
                    fontFamily: 'cocoGoth',
                    color: '#fff',
                    fontSize: '20px',
                    textDecoration: 'none',
                    backgroundColor: '#202230',
                    width: '290px',
                    padding: '15px 0',
                    margin: '30px 0',
                },
                '& img': {
                    margin: 0
                }
            }
        },
        '& .hero_image_container': {
            position: "relative",
            width: '35%',
            $nest: {
                '& img': {
                    width: '550px',
                    position: 'absolute',
                    top: '20%',
                    left: '120px',
                    boxShadow: '-10px 10px 0px 0px #B3F12D'
                }
            }
        },
    }

})

export default HeroSeccionStyle
