import { style } from "typestyle";

const HomeHowItWorkStyle = style({
    // backgroundColor: 'red',
    height: '80vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    $nest: {
        '& .title': {
            $nest: {
                '& span': {
                    fontFamily: 'GothamLight'
                },
            }
        },
        '& .steps': {
            $nest: {
                '& .circle': {
                    position: 'relative',
                    height: '150px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    width: '150px',
                    borderRadius: '50%',
                    // backgroundColor: 'red',
                    border: 'solid black 3px',
                    $nest: { 
                        '& .circle_color': {
                            height: '105%',
                            width: '105%',
                            borderRadius: '50%',
                            position: 'absolute',
                            left: -10,
                            top: -10,
                            backgroundColor: '#33DFCA',
                            zIndex: -1
                        },
                        '& .num': {
                            zIndex: 1,
                            fontFamily: 'GothamBold',
                            fontSize: '36px'
                        }
                    }
                }
            }
        }
    }
})

export default HomeHowItWorkStyle
