import { style } from "typestyle";

const PerfilStyle = style({
    padding: '50px 0',
    marginTop: '100px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    $nest: {
        '& .title': {
            fontFamily: 'GothamBold',
            $nest: {
                '& span': {
                    fontFamily: 'GothamLight',
                }
            }
        },
        '& .message-input': {
            width: '80%',
            $nest: {
                '& label': {
                    display: 'block',
                    fontFamily: 'GothamBold',
                    fontSize: '26px',
                    margin: '20px 0'
                },
                '& textarea': {
                    display: 'inline-block',
                    fontFamily: 'GothamLight',
                    fontSize: '20px',
                    width: '100%',
                    border: 'solid #242535 5px',
                    height: '380px',
                    marginBottom: '30px',
                    textAlign: 'left',
                    padding: '0 10px',
                    outline: 'none',
                },
                '& button': {
                    float: 'right',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    maxHeight: '120px',
                    backgroundColor: '#00EF8E',
                    minWidth: '220px',
                    border: 'none',
                    padding: '20px 0',
                    margin: '0 20px',
                    color: '#273F4F',
                    fontFamily: 'GothamBold',
                    fontSize: '16px',
                    cursor: 'pointer',
                    $nest: {
                        '& img': {
                            width: '20px',
                            marginRight: '20px'
                        }
                    }
                }
            }
        },

        '& .account-info': {
            width: '100%',
            $nest: {
                '& h1': {
                    borderBottom: 'solid black 5px',
                    width: '80%',
                    margin: 'auto',
                    paddingBottom: '20px',
                },
                '& .wrapper': {
                    display: 'flex',
                    width: '80%',
                    $nest: {
                        '& .info': {
                            width: '50%',
                            $nest: {
                                '& .mid': {
                                    display: 'inline-block',
                                    width: '50%',
                                    $nest: {
                                        '& label': {
                                            display: 'block',
                                            fontFamily: 'GothamBold',
                                        },
                                        '& input': {
                                            width: '100%',
                                            border: 'solid black 2px',
                                            height: '40px',
                                            outline: 'none',
                                        },
                                    }
                                },
                                '& .all': {
                                    display: 'inline-block',
                                    width: '100%',
                                    $nest: {
                                        '& label': {
                                            display: 'block',
                                            fontFamily: 'GothamBold',
                                        },
                                        '& input': {
                                            width: '100%',
                                        }
                                    }
                                },
                            }
                        },
                        '& .photo-container': {
                            width: '50%',
                        },
                    }
                }
            }
        }
    }
})

export default PerfilStyle
