import React, { useEffect, useRef } from 'react';
import HomeMembershipSeccionStyle from '../../../styles/pages/Home/HomeMembershipSeccionStyle/HomeMembershipSeccionStyle'

import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';

import ray from '../../../assets/images/Home/HomeMembershipSeccion/ray.svg'
import centerImage from '../../../assets/images/Home/HomeMembershipSeccion/MbgImage.png'



function addClass(element, addClass) { 
    element.classList.add(addClass);
  } 
  function removeClass(element, removeClass) { 
    element.classList.remove(removeClass);
  } 


function HomeMembershipSeccion() {

    gsap.registerPlugin(ScrollTrigger);
    const Ref = useRef(null);

    useEffect(() => {

        // gsap.set(".seccion2_wrapper", { y: 175, yPercent: -50, yPercent: -50, opacity: 0 });
        gsap.to(".info_seccion", {
            left: -400,
            duration: 6,
            opacity: 1,
            scrollTrigger: {
              trigger: ".seccion1_wrapper",
              start: "center center",
              end: "bottom bottom",
              scrub: true,
              markers: true
            }
          });


          const transition_ele = gsap.utils.toArray('[data-transition]');

          transition_ele.forEach((ele) => {  

            ele.style.transitionDuration = ele.getAttribute('data-transition-duration') ? ele.getAttribute('data-transition-duration') : '1s'; 
            ele.style.transitionDelay = ele.getAttribute('data-transition-delay') ? ele.getAttribute('data-transition-delay') : '1s';  

          }); 

          const animation_ele = gsap.utils.toArray('[data-animation]');

          animation_ele.forEach((ele) => { 

            ele.style.animationDuration = ele.getAttribute('data-animation-duration') ? ele.getAttribute('data-animation-duration') : '1s'; 
            ele.style.animationDelay = ele.getAttribute('data-animation-delay') ? ele.getAttribute('data-animation-delay') : '1s';   

          }); 

          const pageContainer = document.querySelector(".smooth-container");

            addClass(pageContainer, 'LocomotiveScroll'); 


          // ScrollTrigger && LocomotiveScroll

          gsap.registerPlugin(ScrollTrigger, ScrollToPlugin);

          const scroller = new LocomotiveScroll({
            el: pageContainer,
            smooth: true, 
            tablet: {
               breakpoint: 0
            }
          });

          scroller.on('call', (value, way, obj)  => {
            // Using modularJS
            console.log(obj);
          }); 

          ScrollTrigger.scrollerProxy(pageContainer, {
            scrollTop(value) {
              return arguments.length
                ? scroller.scrollTo(value, 0, 0)
                : scroller.scroll.instance.scroll.y;
            },
            getBoundingClientRect() {
              return {
                left: 0,
                top: 0,
                width: window.innerWidth,
                height: window.innerHeight
              };
            },
            pinType: pageContainer.style.transform ? "transform" : "fixed"
          });

          const horizontalSections = gsap.utils.toArray('.scroll-horizontal')

          horizontalSections.forEach(function (sec, i) {  

            var thisPinWrap = sec.querySelector('.pin-wrap');
            var thisAnimWrap = thisPinWrap.querySelector('.animation-wrap');  

            var dif = 0;
            if( sec.offsetWidth < window.innerWidth ){
              dif = sec.offsetWidth;
            }

            var getToValue = () => -(thisAnimWrap.scrollWidth - window.innerWidth + dif); 

            let scrollTween = gsap.fromTo(thisAnimWrap, { 
              x: () => thisAnimWrap.classList.contains('to-right') ? 0 : getToValue() 
            }, { 
              x: () => thisAnimWrap.classList.contains('to-right') ? getToValue() : 0, 
              ease: "none",
              scrollTrigger: {
                scroller: pageContainer, //locomotive-scroll
                trigger: sec,   
                start: "top top",
                end: () => "+=" + (thisAnimWrap.scrollWidth - window.innerWidth),
                pin: thisPinWrap,
                invalidateOnRefresh: true,
                //anticipatePin: 1,
                scrub: 0.1,
                // markers: true,
              }
            });  


            const boxes = gsap.utils.toArray('.fadeInUp', sec);

            boxes.forEach((box, i) => { 
               ScrollTrigger.create({
                  trigger: box,
                   markers: true,
                   // scroller: pageContainer, //locomotive-scroll
                    // trigger: ".green",
                    containerAnimation: scrollTween,
                    // start: "center 65%",
                    // end: "center 51%",
                    onEnter: () => console.log("enter"),
                    onLeave: () => console.log("leave"),
                    onEnterBack: () => console.log("enterBack"),
                    onLeaveBack: () => console.log("leaveBack"),
                    onToggle: self => console.log("active", self.isActive),
                    // id: "4"
                  });

            }); 

          });   




            scroller.on("scroll", ScrollTrigger.update);
            ScrollTrigger.addEventListener("refresh", () => scroller.update()); //locomotive-scroll
            ScrollTrigger.refresh();

        }, [])

    return (
        <div ref={Ref} className={HomeMembershipSeccionStyle}>
            <div className='seccion1_wrapper'>
                <div className='title_seccion'>
                    <img className='ray' src={ray}/>
                    <h1>
                        Sube <br/> el <br/> nivel <br/> a tu <br/> rutina
                    </h1>
                </div>

                <div className='info_seccion'>
                    <h1 className='info_title'>Sumérgete en el mundo <br/> <span>Beauty Club </span></h1>
                    <h4 className='info_subtitle'>Cinco productos <br/> <span> seleccionados para ti. </span></h4>
                    <img className='center_image' src={centerImage}/>
                    <ul>
                        <li>1 Full size + 4 Complementarios </li>
                        <li>Productos recomendados de las mejores marcas </li>
                        <li>Nuestros especialistas seleccionan los productos especialmente para ti </li>
                        <li>Recibes al menos 60% más de lo que pagas en cada box  </li>
                    </ul>

                    <button className='membership_button'>MEMBRESIAS</button>
                    <div className='membership_seccion'>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default HomeMembershipSeccion
