import { style } from "typestyle";

const PerfilStyle = style({
    padding: '50px 0',
    marginTop: '100px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    $nest: {
        '& .title': {
            fontFamily: 'GothamBold',
            $nest: {
                '& span': {
                    fontFamily: 'GothamLight',
                }
            }
        },
        '& .message-input': {
            width: '80%',
            $nest: {
                '& label': {
                    display: 'block',
                    fontFamily: 'GothamBold',
                    fontSize: '26px',
                    height: '380px',
                    margin: '20px 0'
                },
                '& input': {
                    width: '100%',
                    border: 'solid #242535 5px'
                },
                '& button': {
                    float: 'right',
                }
            }
        }
    }
})

export default PerfilStyle
