import { style } from "typestyle";

const HomeHowItWorkStyle = style({
    // backgroundColor: 'red',
    height: '80vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    $nest: {
        '& .title': {
            $nest: {
                '& span': {
                    fontFamily: 'GothamLight'
                },
            }
        },
        '& .steps_container': {
            display: 'flex',
            width: '70%',
            justifyContent: 'space-evenly',
            $nest: {
                '& .steps': {
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    textAlign: 'center',
                    justifyContent: 'center',
                    maxWidth: '250px',
                    $nest: {
                        '& .circle': {
                            position: 'relative',
                            height: '150px',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            width: '150px',
                            borderRadius: '50%',
                            // backgroundColor: 'red',
                            border: 'solid black 3px',
                            $nest: { 
                                '& .circle_color': {
                                    height: '105%',
                                    width: '105%',
                                    borderRadius: '50%',
                                    position: 'absolute',
                                    left: -10,
                                    top: -10,
                                    backgroundColor: '#33DFCA',
                                    zIndex: -1
                                },
                                '& .num': {
                                    zIndex: 1,
                                    fontFamily: 'GothamBold',
                                    fontSize: '36px'
                                }
                            }
                        },
                        '& .step_title': {
                            fontFamily: 'GothamLight',
                            color: 'rgba(153, 38, 247, 1)',

                        }
                    }
                }
            }
        },
    }
})

export default HomeHowItWorkStyle
