import { style } from "typestyle"


const QuizFormStyle = style({
    backgroundColor: 'white',
    height: '100vh',
    $nest: {
        '& .navbar': {
            display:'flex',
            justifyContent: 'space-between',
            padding: '10px 50px',
            height: '100px',
            borderBottom: 'solid #00FF52 5px',
            $nest: {
                '& img': {
                    width: '150px'
                },
                '& h1': {
                    fontFamily: 'GothamLight',
                    textTransform: 'uppercase',
                    fontSize: '44px',
                }
            }
        },
        '& .step1': {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            $nest: {
                '& .user_name': {
                    fontFamily: 'Alpha',
                    lineHeight: 0,
                    marginTop: '150px',
                    fontSize: '130px',
                    textTransform: 'uppercase',
                },
                '& h2': {
                    margin: 0,
                    fontFamily: 'GothamBold',
                    fontSize: '44px',
                },
                '& h3': {
                    margin: '20px 0',
                    fontFamily: 'GothamLight',
                    fontSize: '35px',
                },
                '& p': {
                    textAlign: 'center',
                    fontFamily: 'GothamBold',
                    fontSize: '21px',
                },
                '& .question-title': {
                    fontSize: '32px',
                    fontFamily: 'GothamBold',
                    margin: '20px 0',
                },
                '& .paragraph1': {
                    color: '#AE50E2',
                    marginBottom: '40px'
                },
                '& .answers-wrapper': {
                    display: 'flex',
                    margin: '30px 0',
                    width: '60%',
                    justifyContent: 'space-between'
                }
            },
            '& .pagination-button': {
                fontFamily: 'GothamBold',
                backgroundColor: 'red',
                width: '290px',
                padding: '30px 0',
            }
        }
    }
})
export default QuizFormStyle
