import { style } from "typestyle";

const QuestionStyle = style({
    width: '50%',
    $nest: {
        '& .myAccordionHeader': {
            backgroundColor: '#A125F4',
            border: 'solid 2px white',
            $nest: {
                '& h3': {
                    margin: 'auto',
                    color: 'white',
                    fontFamily: 'GothamBold',
                    fontSize: '30px',
                }
            }
        },
        '& .myAccordionContent': {
            backgroundColor: '#A125F4',
            border: 'solid 2px white',
            borderTop: 'none',
            $nest: {
                '& p': {
                    color: 'white',
                    fontFamily: 'GothamLight',
                    fontSize: '20px',
                    textAlign: 'center',
                }
            }
        },
    }
})

export default QuestionStyle
