import React, { useEffect, useRef } from 'react';
import BenefitParallaxGalleryStyle from '../../styles/components/BenefitParallaxGalleryStyle/BenefitParallaxGalleryStyle'
import ParallaxGaleryItem from './ParallaxGaleryItem'

import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';

function BenefitParallaxGallery () {
    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
                // I set opacity: 0 in the CSS to avoid the flash of unstyled content. Also, it's always best to set your transforms directly via GSAP instead of just in the CSS. Faster performance and more accurate.
        gsap.set(".benefit_wrapper", {opacity: 0});
        gsap.to(".benefit_wrapper", {
        y: -160,
        opacity: 1,
        ease: "expo",
        duration: 2
        });

    }, [])

    return (
        <div className={`${BenefitParallaxGalleryStyle} benefit_wrapper`}>
            <div className='item' >
                <ParallaxGaleryItem position='right'></ParallaxGaleryItem>
            </div>
