                <ParallaxGaleryItem position='right'></ParallaxGaleryItem>
            </div>
            <div className='item' >
                <ParallaxGaleryItem position='right'></ParallaxGaleryItem>
            </div>
        </div>
    )
}

export default BenefitParallaxGallery
