import { style } from "typestyle";

import cursorImage from '../../../../assets/images/heroBannerimage2.png';

const ProductSeccionStyle = style({
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: "column",
    width: '100%',
    height: '100vh',
    cursor: 'none',
    // cursor: `url(${cursorImage}), auto`,
    $nest: {
        '& .title': {
            margin: '20px 0',
            fontFamily: 'cocoGoth',
            fontSize: '36px',
            lineHeight: 1,
            $nest: {
                '& span': {
                    color: '#BC49EA'
                }
            }
        },
        '& .subtitle': {
            margin: 0,
            marginBottom: '60px',
            fontFamily: 'cocoGothLight',
            fontSize: '18px',
            lineHeight: 1
        },
        '& .productsContain': {
            display: 'flex',
        },
        '& .cursor-dot': {
            width: '8px',
            height: '8px',
            backgroundColor: '#dc5656',
        },

        '& .cursor-dot-outline': {
            width: '40px',
            height: '40px',
            backgroundColor: 'rgba(229, 128, 128, 0.5)',
        },


        '& .cursor-dot, .cursor-dot-outline ': {
          pointerEvents:'none',
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          borderRadius: '50%',
          opacity: 1,
          transition: 'opacity 0.3s ease-in-out, transform 0.3s ease-in-out',
        }

    }

})

export default ProductSeccionStyle
