import { style } from "typestyle";

const PerfilStyle = style({
    padding: '50px 0',
    marginTop: '100px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    $nest: {
        '& .title': {
            fontFamily: 'GothamBold',
            $nest: {
                '& span': {
                    fontFamily: 'GothamLight',
                }
            }
        },
        '& .message-input': {
            width: '80%',
            $nest: {
                '& input': {
                    width: '100%',
                    border: 'solid #242535 7px'
                }
            }
        }
    }
})

export default PerfilStyle
