import { style } from "typestyle";

const TabsComponentStyle = style({
  width: '100vw',
  $nest: {
    '& .tabs-menu': {
        width: '100%',
        display: 'flex',
        justifyContent: 'space-between',
        $nest: {
            '& .tab': {
                textAlign: 'center',
                fontFamily: 'Satoshi',
                fontWeight: 900,
                padding: '20px',
                width: '100%',
                listStyle: 'none',
                fontSize: '20px',
                zIndex: 1000,
                cursor: 'pointer',
                $nest: {
                    '@media screen and (max-width: 480px)': {
                        minWidth: '180px',
                        padding: '20px 0',

                    }
                }
            },
            '@media screen and (max-width: 480px)': {
                overflowX: 'scroll',
            }
        }
    },
    '& .tab-content': {
        position: 'relative',
    },
    '& .tab-content-text': {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '40vh',
        fontFamily: 'Satoshi',
        fontSize: '35px',
        maxWidth: '800px',
        margin: 'auto',
        fontWeight: 400,
        $nest: {
            '@media screen and (max-width: 890px)': {
                width: '80%',
            }
        }
    },
}
})

export default TabsComponentStyle
