import { style } from "typestyle";

import bgImage from '../../../../assets/images/Home/HeroSeccion/Biury_Fotos-01.png'


const HeroSeccionStyle = style({
    position: "relative",
    backgroundImage: `url(${bgImage})`,
    paddingTop: '80px',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    display: 'flex',
    height: '120vh',
    $nest: {
        '& .hero_container': {
            $nest: {
                '& .hero_image_wrapper': {
                    overflow: 'hidden',
                    width: '550px',
                    position: 'absolute',
                    padding: '20px',
                    paddingRight: 0,
                    top: '20%',
                    left: '120px',
                    position: 'relative',
                    zIndex: 2,
                    $nest: {
                        '& img': {
                            // visibility: 'hidden'
                            border: 'none',
                            boxShadow: '-10px 10px 0px 0px #B3F12D',
                        },
                        '& .mask': {
                            position:'absolute',
                            bottom: 22,
                            width: '97%',
                            right: 0,
                            height: '588px',
                            backgroundColor: '#6962FD',
                        },
                        '& .mask2': {
                            position:'absolute',
                            bottom: 0,
                            width: '100%',
                            height: '100%',
                            backgroundColor: '#fff',
                        },
                    }
                }
            }
        },
    }

})

export default HeroSeccionStyle
