import { style } from "typestyle";

const PerfilStyle = style({
    padding: '50px 0',
    marginTop: '100px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    $nest: {
        '& .title': {
            fontFamily: 'GothamBold',
            $nest: {
                '& span': {
                    fontFamily: 'GothamLight',
                }
            }
        },
        '& .message-input': {
            width: '80%',
            $nest: {
                '& label': {
                    display: 'block',
                    fontFamily: 'GothamBold',
                    fontSize: '26px',
                    margin: '20px 0'
                },
                '& input': {
                    display: 'inline-block',
                    width: '100%',
                    border: 'solid #242535 5px',
                    height: '380px',
                    marginBottom: '30px',
                    textAlign: 'left',
                    padding: '0.4em 0.4em 0.4em 0',
                    outline: 'none',
                    appearance: 'textfield',
                    "-webkit-appearance": 'textfield',
                    "-moz-appearance": 'textfield',
                },
                '& button': {
                    float: 'right',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    maxHeight: '120px',
                    backgroundColor: '#00EF8E',
                    minWidth: '220px',
                    border: 'none',
                    padding: '20px 0',
                    margin: '0 20px',
                    color: '#273F4F',
                    fontFamily: 'GothamBold',
                    fontSize: '16px',
                    $nest: {
                        '& img': {
                            width: '20px',
                            marginRight: '20px'
                        }
                    }
                }
            }
        }
    }
})

export default PerfilStyle
