import React, { useEffect, useRef } from 'react';
import HomeTestSeccionStyle from '../../../styles/pages/Home/TestSeccionStyle/TestSeccionStyle';
import HomeTestBgImage from '../../../assets/images/Home/HomeTestSeccion/HometestImage.png';
import { Link } from 'react-router-dom';
import gsap from 'gsap';

import Animation from '../../../animation';

import star from '../../../assets/images/Home/HomeTestSeccion/star.svg';
import faceIcon from '../../../assets/images/Home/HomeTestSeccion/faceIcon.svg';


import ScrollTrigger from 'gsap/ScrollTrigger';


function addClass(element, addClass) { 
    element.classList.add(addClass);
  } 
  function removeClass(element, removeClass) { 
    element.classList.remove(removeClass);
  } 




function TestSeccion() {

    const transition_ele = gsap.utils.toArray('[data-transition]');

    transition_ele.forEach((ele) => {  
        ele.style.transitionDuration = ele.getAttribute('data-transition-duration') ? ele.getAttribute('data-transition-duration') : '1s'; 
        ele.style.transitionDelay = ele.getAttribute('data-transition-delay') ? ele.getAttribute('data-transition-delay') : '1s';  
    }); 

      const animation_ele = gsap.utils.toArray('[data-animation]');

      animation_ele.forEach((ele) => { 

        ele.style.animationDuration = ele.getAttribute('data-animation-duration') ? ele.getAttribute('data-animation-duration') : '1s'; 
        ele.style.animationDelay = ele.getAttribute('data-animation-delay') ? ele.getAttribute('data-animation-delay') : '1s';   

      }); 

    const pageContainer = document.querySelector(".smooth-container");

    addClass(pageContainer, 'LocomotiveScroll'); 

    useEffect(() => {

        gsap.to(".box", {
            scrollTrigger: ".box", // start the animation when ".box" enters the viewport (once)
            x: 300
          });
        }, [])


    return(
        <div className={`${HomeTestSeccionStyle} smooth-container`}>
            <div className='home_test_title__container'>
                {/* <h4  className='home_test_subtitle1' >EL TEST IDEAL</h4> */}
                <h1 data-scroll data-scroll-repeat data-transition="fadein" data-transition-delay=".3s">Horizontal</h1>
                <h1 className='home_test_title'>Rapidos</h1>
                <h1 className='home_test_title'>DIVERTIDO.</h1>
                <h1 className='home_test_title'>fácil.</h1>
                <h4 className='home_test_subtitle2'>Cúentanos lo que necesitas y <br/> <span>nosotros hacemos el resto </span> </h4>

                <Link className='home_test_button'>Haz nuestro test</Link>
                <img className='star' src={star}/>
            </div>
                  <Animation></Animation> 
            <div  className='home_test_image__container'>
                {/* <img className='bgImage' src={HomeTestBgImage}/>   */}

                {/* <div className='faceIcon iconOne'>
                    <div className='circle'>
                        <img src={faceIcon}/>
                    </div>
                </div>
                <div className='faceIcon iconTwo'>
                    <div className='circle'>
                        <img src={faceIcon}/>
                    </div>
                </div>
                <div className='faceIcon iconTree'>
                    <div className='circle'>
                        <img src={faceIcon}/>
                    </div>
                </div>  */}
            </div>
        </div>
    )
}

export default TestSeccion;
