import { style } from "typestyle";

const MembershipCardStyle = style({
    // position: 'absolute',
    display: 'flex',
    justifyContent: 'space-between',
    flexDirection: 'column',
    alignItems: 'center',
    paddingTop: '150px',
    boxSizing: 'border-box',
    height: '100%',
    width: '400px',
    borderRight: 'solid 5px red',
    background: 'white',
    color: 'black',
    textAlign: 'center',
    $nest: {
        '& .title': {
            fontFamily: 'GothamBold',
            fontSize: '32px',
            marginRight: '45%',
            margin: 0,
        },
        '& .decorated_title': {
            marginTop: '5px'
        },
        '& .price': {
            fontFamily: 'GothamBold',
            fontSize: '26px',
        },
        '& .promo_text': {
            fontFamily: 'GothamMediumItalic',

        }
    }
})

export default MembershipCardStyle
