import { style } from "typestyle";

import bgHeroImage from '../../../assets/images/Sellers/bgHeroImageSellers.png'


const SellersStyle = style({
    $nest: {
        '& .hero-image': {
            backgroundImage: `url(${bgHeroImage})`,
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            position: 'relative',
            background: 'red',
            height: '100vh',
            marginTop: '100px',
            $nest: {
                '& .wave': {
                    position: 'absolute',
                    bottom: 0
                },
            }
        },
        '& .main-wrapper': {
            $nest: {
                '& .main': {
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    $nest: {
                        '& .title': {
                            fontFamily: 'GothamBold',
                            fontSize: '85px',
                        },
                        '& .line': {
                            width: '40%',
                        },
                        '& .pharagrap': {
                            fontFamily: 'GothamLight',
                            fontSize: '35px',
                            textAlign: 'center',
                        },
                        '& .best-sellers-month': {
                            width: '90%',
                            margin: '100px 0',
                            $nest: {
                                '& .month-title': {
                                    margin: '50px 0'
                                },
                                '& .products-container': {
                                    display: 'grid',
                                    gridTemplateColumns: '280px 280px 280px 280px',
                                    gridGap: '100px 50px',
                                    marginBottom: '200px',
                                }
                            }
                        },
                        '& .brands-title': {
                            fontFamily: 'GothamBold',
                            fontSize: '52px',
                            margin: '100px 0',
                            $nest: {
                                '& span': {
                                    fontFamily: 'GothamLight',
                                }
                            }
                        },

                        '& .brands-wrapper': {
                            width: '80%',
                            display: 'grid',
                            gap: '100px 0',
                            gridTemplateColumns: '1fr 1fr 1fr',
                            $nest: {
                                '& img': {
                                    width: '60%',
                                    margin: 'auto'
                                }
                            }
                        }
                    }
                },
                '& .reviews-seccion': {
                    backgroundColor: '#242535',
                    padding: '40px 0',
                    height: '100vh',
                    display: 'flex',
                    flexDirection: "column",
                    justifyContent: 'center',
                    alignItems: 'center',
                    $nest: {
                        '& .title': {
                            paddingTop: '120px',
                            color: '#67FF6C',
                            zIndex: 2,
                            $nest: {
                                '& span': {
                                    color: '#B3F12D'
                                }
                            }
                        },
                        '& .subtitle': {
                            color: 'white',
                            margin: 0,
                            fontSize: '18px',
                            fontFamily: 'CocoGothLight',
                            fontWeight: 400,
                            $nest: {
                                '& span': {
                                    fontWeight: 700,
                                    fontFamily: 'CocoGothSemiBold',
                                }
                            }
                        },
                        '& button': {
                            fontSize: '20px',
                            fontFamily: 'GothamLight',
                            backgroundColor: '#FF7065',
                            border: 'none',
                            padding: '20px',
                            margin: 'auto',
                            marginBottom: '280px',
                            $nest: {
                                '& span': {
                                    fontFamily: 'GothamBold',
                                }
                            }
                        }
                    }
                }
            },
        },
    }
})

export default SellersStyle
