import { style } from "typestyle";

const CurrentProductItemStyle = style({
    $nest: {
        '& .image-contain': {
            position: 'relative',
            height: '360px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: '#F3EFED',
            $nest: {
                '& .image-border': {
                    position: 'absolute',
                    top: 0,
                    left: 10,
                    width: '100%',
                    height: '100%',
                    border: 'solid black 3px'
                }
            }
        },
    }
})

export default CurrentProductItemStyle
