import { style } from "typestyle";

const TabsComponentStyle = style({
  $nest: {
    '& .container': {
      backgroundColor: '#eeeded',
      border: '1px solid rgba(0, 0, 0, 0.3)',
      $nest: {
        '& .tab_list': {
          height: '50px',
          display: 'flex',
          listStyle: 'none',
          fontSize: '18px',
          padding: '0',
          margin: '0',
        },
        '& .tabs': {
          width: '50%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          background: '#80808013',
          position: 'relative',
          cursor: 'pointer',
        },
        '& .active_tabs': {
          width: '50%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          background: '#80808013',
          position: 'relative',
          cursor: 'pointer',
          $nest: {
            '&::before': {
              content: `""`,
              display: 'block',
              position: 'absolute',
              top: '-4px',
              left: '50%',
              transform: 'translateX(-50%)',
              width: 'calc(100% + 2px)',
              height: '4px',
              background: '#5893f1',
            }
          }
        },
        '& .content_container': {
          $nest: {
            '& .content': {
              backgroundColor: '#eeeded',
              display: 'none',
              padding: '10px',
            },
            '& .active_content': {
              display: 'flex',
            },
          }
        }
      }
    },
  },
})

export default TabsComponentStyle
