import { style } from "typestyle";

const QuestionStyle = style({
    backgroundColor: 'red',
    width: '50%',
    $nest: {
        '& .myAccordionHeader': {
            backgroundColor: '#A125F4',
            border: 'solid 2px white',
            margin: 'auto'
        },
        '& .myAccordionContent': {
            backgroundColor: '#A125F4',
            border: 'solid 2px white',
            borderTop: 'none'
        },
    }
})

export default QuestionStyle
