import { style } from "typestyle";
import bgBanner from '../../../../assets/images/Home/HeroSeccion/banner/bgBanner.png'

const BenefitSeccionStyle = style({
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    position: "relative",
    overflow: 'hidden',
    $nest: {
        '& .membership_title_container': {
            display: 'grid',
            gridTemplateAreas: "'. pos1 .' 'pos2 pos3 pos4' '. pos5 .'",
            gridTemplateColumns: '300px 300px 300px',
            gridAutoRows: '1fr 1fr',
            height: '80%',
            marginLeft: '12%',
            $nest: {
                '& .position1': {
                    gridArea: 'pos1'
                },
                '& .position2': {
                    gridArea: 'pos2'
                },
                '& .position3': {
                    gridArea: 'pos3',
                    position: "relative",
                    $nest: {
                        '& .text': {
                            position: "absolute",
                            top: -100,
                            left: -150,
                            textAlign: "center",
                            fontFamily: 'CocoGothLight',
                            fontSize: '62px',
                            $nest: {
                                '& span': {
                                    fontFamily: 'CocoGothSemiBold',
                                    lineHeight: 0,
                                    fontSize: '95px',
                                }
                            }
                        }
                    }
                },
                '& .position4': {
                    gridArea: 'pos4'
                },
                '& .position5': {
                    gridArea: 'pos5'
                },
            }
        },
        '& .hero_banner': {
            position: 'absolute',
            width: '100%',
            padding: '30px 0',
            display: 'flex',
            justifyContent: 'space-evenly',
            alignItems: 'center',
            bottom: '0',
            backgroundImage: `url(${bgBanner})`,
            backgroundColor: 'white',
            zIndex: 1
        }
    }
})


export default BenefitSeccionStyle
