import { style } from "typestyle";

const HowItWorkStyle = style({
    position: 'relative',
    backgroundColor: 'white',
    margin: '100px 0',
    paddingTop: '500px',
    $nest: {
        '& .top-wave': {
            position: 'absolute',
            top: 0,
        },
        '& .top-title': {
            position: 'absolute',
            textAlign: 'center',
            fontFamily: 'GothamBold',
            color: 'white',
            fontSize: '67px',
            top: 45,
            left: 0,
            right: 0,
            margin: 'auto'
        },
        '& .steps-container': {
            position: 'relative',
            $nest: {
                '& .step': {
                    display: 'flex',
                    width: '60%',
                    $nest: {
                        '& .number': {
                            position: 'relative',
                            width: '30%',
                            $nest: {
                                '& .border': {
                                    position: 'absolute',
                                },
                                '& .color': {
                                    position: 'absolute',
                                }
                            }
                        },
                        '& .info': {
                            width: '70%',
                            $nest: {
                                '& h1': {
                                    fontFamily: 'GothamBold',
                                    fontSize: '46px',
                                    $nest: {
                                        '& span': {
                                            fontFamily: 'GothamLight',
                                        }
                                    }
                                },
                                '& p': {
                                    fontFamily: 'GothamLight',
                                    fontSize: '22px',
                                },
                            }
                        }
                    }
                },
                '& .step1': {
                    marginLeft: '50%',
                    $nest: {
                        '& .number': {
                            position: 'relative',
                            width: '30%',
                            $nest: {
                                '& .border': {
                                    position: 'absolute',
                                    top: 30,
                                },
                                '& .color': {
                                    position: 'absolute',
                                    top: 55,
                                    left: -10,
                                }
                            }
                        },
                    }
                },
                '& .step2': {
                    marginLeft: '10%',
                    $nest: {
                        '& .number': {
                            position: 'relative',
                            width: '30%',
                            $nest: {
                                '& .border': {
                                    position: 'absolute',
                                    top: 40,
                                    left: -40,
                                },
                                '& .color': {
                                    position: 'absolute',
                                    top: 20,
                                    left: -50,
                                }
                            }
                        },
                    }
                },
            }
        }
    }
})

export default HowItWorkStyle
